import {useState} from "react";

function TodoForm(){
    const [name, setName] = useState('');
    const [product, setProduct] = useState('');
    const [lists, setList] = useState([]);

    const handleSubmit = event => {
        event.preventDefault(); // 👈️ prevent page refresh
        setList((lists) => [...lists, {  name: name, product: product }]);
        document.getElementById("todoForm").reset();
    };

    const listingItems = lists.map((list) =>
        <tr>
            <td scope="col" className="px-6 py-3">{list.name}</td>
            <td scope="col" className="px-6 py-3">{list.product}</td>
        </tr>
    );

    const exportPDF = () => {
        const unit = "pt";
        const size = "A4"; // Use A1, A2, A3 or A4
        const orientation = "portrait"; // portrait or landscape

        const marginLeft = 40;
        const doc = new jsPDF(orientation, unit, size);

        doc.setFontSize(15);

        const title = "My Awesome Report";
        const headers = [["PRODUCT", "QTY"]];

        const data = this.state.people.map(elt=> [elt.name, elt.profession]);

        let content = {
            startY: 50,
            head: headers,
            body: data
        };

        doc.text(title, marginLeft, 40);
        doc.autoTable(content);
        doc.save("report.pdf")
    }


    return(
        <div>
            <div className="grid grid-cols-2 gap-2">
                <form id={"todoForm"} className="w-full max-w-lg" onSubmit={handleSubmit}>
                <div className="flex flex-wrap -mx-3 mb-6">
                    <h1 className={'mx-auto mb-5 text-xl font-bold'}>Todo List Form</h1>
                    <div className="w-full  px-3 mb-12 md:mb-2">
                        <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
                               htmlFor="grid-first-name">
                            İsteyen
                        </label>
                        <input
                            className="appearance-none block w-full border-black text-gray-700 border  rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
                            id="grid-first-name" onChange={event => setName(event.target.value)} type="text" placeholder="Adınız ve Soyadınız" />
                    </div>
                    <div className="w-full  px-3 mb-12 md:mb-2">
                        <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
                               htmlFor="grid-who">
                            Ürün
                        </label>
                        <input
                            className="appearance-none block w-full border-black text-gray-700 border  rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
                            id="grid-who" onChange={event => setProduct(event.target.value)} type="text" placeholder="Ne Lazım?" />
                    </div>
                    <div className="w-full  px-3 mb-12 md:mb-0">
                        <button
                            className="w-full bg-black hover:bg-blue-600 text-white font-bold py-2 px-4 border-b-4 border-black hover:border-blue-500 rounded">
                            Listeye Ekle
                        </button>
                    </div>
                </div>
            </form>
                <div>
                    <h1 className={'text-center mb-5 text-xl font-bold'}>Todo List</h1>
                    <div className={'relative overflow-x-auto shadow-md sm:rounded-lg"'}>
                        <table className={'w-full text-sm text-left text-gray-500 dark:text-gray-400'}>
                            <thead className={'w-full text-sm text-left text-gray-500 dark:text-gray-400'}>
                            <tr>
                                <th scope="col" className="px-6 py-3">
                                    Ad ve Soyad
                                </th>
                                <th scope="col" className="px-6 py-3">
                                    Ürün
                                </th>
                            </tr>
                            </thead>
                            <tbody className={'w-full text-sm text-left text-black text-bold dark:text-gray-400'}>
                                 {listingItems}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default TodoForm;
